r WM\"\n        [131670] = \"#0a30eff\", --\"Heartsbane Vinetwister WM\"\n        [128969] = \"#0a30eff\", --\"Ashvane Commander\"\n        [131586] = \"#0a30ef\", --\"Banquet Steward\"\n        [129527] = \"#0a30ef\", --\"Bilge Rat Buccaneer FH\"\n        [134284] = \"#0a30ef\", --\"Fallen Deathspeaker UR\"\n        [137473] = \"#0a30ef\", --\"Guard Captain Atu KR\"\n        [134232] = \"#0a30ef\", --\"Hired Assassin\"\n        [127111] = \"#0a30ef\", --\"Irontide Oarsman\"\n        [134629] = \"#0a30ef\", --\"Scaled Krolusk Rider\"\n        [127879] = \"#0a30ef\", --\"Shieldbearer of Zul\"\n        [135989] = \"#0a30ef\", --\"Shieldbearer of Zul\"\n        [134139] = \"#0a30ef\", --\"Shrine Templar\"\n        [150160] = \"#0a30ef\", --\"Scrapbone Bully\"\n        [133835] = \"#0a30ef\", --\"Feral Bloodswarmer\"\n        [154758] = \"#0a30ef\", --\"Toxic Monstrosity\"\n        [134417] = \"#0a30ef\", --\"Deepsea Ritualist\"\n        [144298] = \"#0a30ef\", --\"Defense Bot Mk III\"\n        [129602] = \"#0a30ef\", --\"Irontide Enforcer\"\n        [130025] = \"#0a30ef\", --\"Irontide Thug TD\"\n        [128969] = \"#0a30ef\", --\"Ashvane Commander\"\n        [135167] = \"#0a30ef\", --\"Spectral Berserker\"\n        [130025] = \"#0a30ef\", --\"Irontide Thug\"\n        [136214] = \"#0a30ef\", -- \"Windspeaker Heldis\"\n        [155090] = \"#0a30ef\", -- \"Anodized Coilbearer\"\n        [129547] = \"#0a30ef\", -- \"Blacktooth Knuckleduster\"\n        \n        \n        \n        --Light Blue Caster Bois\n        \n        [129600] = \"#20aeef\", --\"Bilge Rat Brinescale\"\n        [130026] = \"#20aeef\", --\"Bilge Rat Seaspeaker\"\n        [129367] = \"#20aeef\", --\"Bilge Rat Tempest SoB\"\n        [122973] = \"#20aeef\", --\"Dazar'ai Confessor AD\"\n        [131812] = \"#20aeef\", --\"Heartsbane Soulcharmer\"\n        [129788] = \"#20aeef\", --\"Irontide Bonesaw\"\n        [131850] = \"#20aeef\", --\"Maddened Survivalist\"\n        [151657] = \"#20aeef\", --\"Bomb Tonk\"\n        [134174] = \"#20aeef\", --\"Shadow-Borne Witch Doctor\"\n        [136470] = \"#20aeef\", --\"Refreshment Vendor\"\n        [135204] = \"#20aeef\", --\"Spectral Hex Priest\"\n        [150297] = \"#20aeef\", --\"Mechagon Renomarlizer\"\n        [133912] = \"#20aeef\", --\"Bloodsworn Defiler\"\n        [122969] = \"#20aeef\", --\"Zanchuli Witch-Doctor\"\n        [144294] = \"#20aeef\", --\"Mechagon Tinkerer\"\n        [127486] = \"#20aeef\", --\"Ashvane Officer\"\n        [134990] = \"#20aeef\", --\"Charged Dust Devil\"\n        [134364] = \"#20aeef\", --\"Faithless Tender\"\n        [134251] = \"#20aeef\", --\"Seneschal M'bara\"\n        [134137] = \"#20aeef\", --\"Temple Attendant\"\n        [129370] = \"#20aeef\", --\"Irontide Waveshaper\"\n        [144071] = \"#20aeef\", --\"Irontide Waveshaper\"\n        [141284] = \"#20aeef\", --\"Kul Tiran Wavetender\"\n        [142293] = \"#20aeef\", --\"Kul Tiran Wavetender\"\n        [128434] = \"#20aeef\", --\"Feasting Skyscreamer\"\n        [150146] = \"#20aeef\", --\"Scrapbone Shaman\"\n        [130661] = \"#20aeef\", --\"Venture Co. Earthshaper\"\n        [133593] = \"#20aeef\", --\"Expert Technician\"\n        [133870] = \"#20aeef\", --\"Diseased Lasher\"\n        [131666] = \"#20aeef\",  --\"Coven Thornshaper\"\n        [150251] = \"#20aeef\",  --\"Pistonhead Mechanic\"\n        [131492] = \"#20aeef\",  --\"Devout Blood Priest\"\n        [135474] = \"#20aeef\",  --\"Thistle Acolyte\"\n        [129553] = \"#20aeef\",  --\"Dinomancer Kish'o\"\n        \n        \n        \n        --Purple Spicy Bois\n        \n        [135241] = \"#f149ff\", --\"Bilge Rat Pillager\"\n        [129601] = \"#f149ff\", --\"Cutwater harpooner\"\n        [131818] = \"#f149ff\", --\"Marked Sister\"\n        [130488] = \"#f149ff\", --\"Mech Jockey\"\n        [137830] = \"#f149ff\", --\"Pallid Gorger\"\n        [131685] = \"#f149ff\", --\"Runic Disciple\"\n        [130909] = \"#f149ff\", --\"Fetid Maggot\"\n        [134896] = \"#f149ff\", --\"Sewer Vicejaw\"\n        [127482] = \"#f149ff\", --\"Sewer Vicejaw\"\n        [144293] = \"#f149ff\", --\"Waste Processing Unit\"\n        [150292] = \"#f149ff\", --\"Mechagon Cavalry\"\n        [135235] = \"#f149ff\", --\"Spectral Beastmaster\"\n        [133685] = \"#f149ff\", --\"Befouled Spirit\"\n        [129552] = \"#f149ff\", --\"Monzumi\"\n        [150165] = \"#f149ff\", --\"Slime Elemental\"\n        [136186] = \"#f149ff\", --\"Tidesage Spiritualist\"\n        [139800] = \"#f149ff\", --\"Galecaller Apprentice\"  \n        [134418] = \"#f149ff\", --\"Drowned Depthbringer\"\n        [150142] = \"#f149ff\", -- \"Scrapbone Trashtosser\"\n        [127757] = \"#f149ff\", -- \"Reanimated Honor Guard\"\n        \n        [159578] = \"#f149ff\", -- \"Exposed Synapse Nzoth\"     \n        \n        \n        \n        \n        \n    }\n    \n    \n    function envTable.UpdateColor (unitFrame)\n        if not unitFrame then return end\n        \n        if envTable.useTargetColor then\n            if unitFrame.namePlateIsTarget then\n                Plater.SetNameplateColor (unitFrame, envTable.targetColor)\n                envTable.HasRaidMark = nil\n                envTable.isTarget = true\n                envTable.wasTarget = false\n                return\n            elseif envTable.isTarget then\n                envTable.isTarget = false\n                envTable.wasTarget = true\n            end\n        end\n        \n        if envTable.useThreatColors then\n            if unitFrame.namePlateThreatIsTanking and not Plater.PlayerIsTank then\n                return\n            end\n            if not unitFrame.namePlateThreatIsTanking and Plater.PlayerIsTank then\n                return\n            end\n        end\n        \n        \n        \n        if envTable.useFocusColor then\n            if UnitIsUnit (unitFrame.PlateFrame.namePlateUnitToken, \"focus\") then\n                Plater.SetNameplateColor (unitFrame, envTable.focusColor)\n                envTable.HasRaidMark = nil\n                envTable.isFocus = true\n                envTable.wasFocus = false\n                return\n            elseif envTable.isFocus then\n                envTable.isFocus = false\n                envTable.wasFocus = true\n            end\n        end\n        \n        -- from \"Color by Raid Mark\"\n        local raidMark = nil\n        if envTable.showRaidMarkColors and unitFrame.unit then\n            raidMark = Plater.GetRaidMark (unitFrame)\n        end\n        if (raidMark and envTable.ColorByMark [raidMark]) then\n            Plater.SetNameplateColor (unitFrame, envTable.ColorByMark [raidMark])\n            envTable.HasRaidMark = true\n        else\n            --attempt to get the color from the unit color list\n            local color = envTable.NpcColors [unitFrame.namePlateUnitNameLower] or envTable.NpcColors [unitFrame.namePlateUnitName] or envTable.NpcColors [unitFrame.namePlateNpcId] or (envTable.colorRares and envTable.RareColors[UnitClassification(unitFrame.namePlateUnitToken)])\n            --if the color exists, set the health bar color\n            if (color) then\n                Plater.SetNameplateColor (unitFrame, color)\n                envTable.HasRaidMark = nil\n            elseif envTable.HasRaidMark or envTable.wasTarget or envTable.wasFocus then\n                if unitFrame.ActorType == \"friendlyplayer\" or unitFrame.ActorType == \"enemyplayer\"  then\n                    Plater.FindAndSetNameplateColor (unitFrame)\n                else\n                    Plater.RefreshNameplateColor (unitFrame)\n                end\n                envTable.HasRaidMark = nil\n                envTable.wasTarget = false\n                envTable.wasFocus = false\n            end\n        end\n    end\n    \nend",
