					["Hooks"] = {
						["Nameplate Updated"] = "function (self, unitId, unitFrame, envTable)\n    \n    \n    --WEBSY2J CUALQUIER DUDA EN MI DISCORD--\n    --COLOCA AQUI LAS HABILIDADES QUE QUIERAS TRACKEAR CON TU PJ--\n    \n    -- TRACKEO PARA HUNTER--\n    local picadura = Plater.NameplateHasAura(unitFrame, \"Picadura de serpiente\")\n    local trampacongelante = Plater.NameplateHasAura(unitFrame, \"Trampa congelante\")\n    local conmocion = Plater.NameplateHasAura(unitFrame, \"Disparo de conmocin\")\n    \n    --COLORES PARA EL HUNTER--\n    if trampacongelante and picadura  then\n        Plater.SetNameplateColor(unitFrame, \"orangered\")    \n        \n    elseif picadura then\n        Plater.SetNameplateColor(unitFrame, \"blue\") \n        \n        if picadura  and conmocion then\n            Plater.SetNameplateColor(unitFrame, \"pink\") \n        end        \n    elseif conmocion  then\n        Plater.SetNameplateColor(unitFrame, \"cyan\")\n        \n        if conmocion and trampacongelante then\n            Plater.SetNameplateColor(unitFrame, \"green\")\n        end\n    elseif trampacongelante then\n        Plater.SetNameplateColor(unitFrame, \"purple\")  \n        \n        \n        \n    else\n        Plater.SetNameplateColor(unitFrame)\n        return    \n    end \nend",
