						["Nameplate Added"] = "function (self, unitId, unitFrame, envTable, modTable)\n    \n    local Healers = modTable.Healers\n    local Tanks = modTable.Tanks\n    \n    unitFrame.RoleTexture:Hide()\n    \n    local _, instanceType = GetInstanceInfo()\n    if instanceType == 'pvp' or instanceType == 'arena' then\n        local name, realm = UnitName(self.unit)\n        realm = (realm and realm ~= '') and modTable.ShortenRealm(realm)\n        if realm then name = name..'-'..realm end\n        \n        if Healers[name] then\n            unitFrame.RoleTexture:Show()\n            unitFrame.RoleTexture.texture = modTable.HealerTexture\n        elseif Tanks[name] then\n            unitFrame.RoleTexture:Show()\n            unitFrame.RoleTexture.texture = modTable.TankTexture\n        end\n    end\n    \n    if modTable.config.debug then\n        unitFrame.RoleTexture:Show()\n        if math.random(2) == 1 then\n            unitFrame.RoleTexture.texture = modTable.HealerTexture\n        else\n            unitFrame.RoleTexture.texture = modTable.TankTexture\n        end\n    end\n    \nend",
						["Initialization"] = "function (modTable)\n    \n    modTable.HealerTexture = [[Interface\\AddOns\\ElvUI\\Core\\Media\\Textures\\Healer]]\n    modTable.TankTexture = [[Interface\\AddOns\\ElvUI\\Core\\Media\\Textures\\Tank]]\n    modTable.Width = 40\n    modTable.Height = 40\n    \n    modTable.Anchor = {\n        side = 7,\n        x = modTable.Width / 2, \n        y = -modTable.Width / 2,\n    }\n    \n    local myrealm = GetRealmName()\n    \n    local a1,a2 = '','[%s%-]'\n    local function ShortenRealm(realm)\n        return gsub(realm, a2, a1)\n    end\n    modTable.ShortenRealm = ShortenRealm\n    \n    local a3 = format('%%-%s', ShortenRealm(myrealm))\n    local function StripMyRealm(name)\n        return gsub(name, a3, a1)\n    end\n    \n    \n    local healerSpecIDs = {\n        65,      --Paladin Holy\n        105,    --Druid Restoration\n        256,    --Priest Discipline\n        257,    --Priest Holy\n        264,    --Shaman Restoration\n        270,    --Monk Mistweaver\n    }\n    \n    local tankSpecIDs = {\n        66,        --Paladin Protection\n        581,    --Demon Hunter Vengeance\n        104,    --Druid Guardian\n        268,    --Monk Brewmaster\n        73,     --Warrior Protection\n        250,    --Death Knight Blood\n    }\n    \n    local Healers, HealerSpecs = {}, {}\n    local Tanks, TankSpecs = {}, {}\n    modTable.Healers = Healers\n    modTable.Tanks = Tanks\n    \n    for _, specID in pairs(healerSpecIDs) do\n        local _, name = GetSpecializationInfoByID(specID)\n        if name and not HealerSpecs[name] then\n            HealerSpecs[name] = true\n        end\n    end\n    \n    for _, specID in pairs(tankSpecIDs) do\n        local _, name = GetSpecializationInfoByID(specID)\n        if name and not TankSpecs[name] then\n            TankSpecs[name] = true\n        end\n    end\n    \n    local function HandleEvent()\n        local _, instanceType = GetInstanceInfo()\n        if instanceType == 'pvp' or instanceType == 'arena' then\n            local numOpps = GetNumArenaOpponentSpecs()\n            if numOpps == 0 then\n                for i = 1, GetNumBattlefieldScores() do\n                    local name, _, _, _, _, _, _, _, _, _, _, _, _, _, _, talentSpec = GetBattlefieldScore(i)\n                    name = name and name ~= UNKNOWN and StripMyRealm(name)\n                    if name then\n                        if HealerSpecs[talentSpec] then\n                            Healers[name] = talentSpec\n                        elseif Healers[name] then\n                            Healers[name] = nil;\n                        end\n                        if TankSpecs[talentSpec] then\n                            Tanks[name] = talentSpec\n                        elseif Tanks[name] then\n                            Tanks[name] = nil;\n                        end\n                    end\n                end\n            elseif numOpps >= 1 then\n                for i = 1, numOpps do\n                    local name, realm = UnitName(format('arena%d', i))\n                    if name and name ~= UNKNOWN then\n                        realm = (realm and realm ~= '') and ShortenRealm(realm)\n                        if realm then name = name..'-'..realm end\n                        \n                        local s = GetArenaOpponentSpec(i)\n                        local _, talentSpec = nil, UNKNOWN\n                        \n                        if s and s > 0 then\n                            _, talentSpec = GetSpecializationInfoByID(s)\n                        end\n                        \n                        if talentSpec and talentSpec ~= UNKNOWN then\n                            if HealerSpecs[talentSpec] then\n                                Healers[name] = talentSpec\n                            end\n                            if TankSpecs[talentSpec] then\n                                Tanks[name] = talentSpec\n                            end\n                        end\n                    end\n                end\n            end\n        end\n    end\n    \n    local function WipeTables()\n        wipe(Healers)\n        wipe(Tanks)\n    end\n    \n    modTable.eventFrame = modTable.eventFrame or GlobalPlaterRoleIconsEventFrame\n    if not modTable.eventFrame then\n        modTable.eventFrame = CreateFrame(\"Frame\", \"GlobalPlaterRoleIconsEventFrame\")\n        modTable.eventFrame:RegisterEvent(\"ARENA_OPPONENT_UPDATE\")\n        modTable.eventFrame:RegisterEvent(\"UPDATE_BATTLEFIELD_SCORE\")\n        modTable.eventFrame:RegisterEvent(\"PLAYER_ENTERING_WORLD\")\n    end\n    \n    modTable.eventFrame:SetScript(\"OnEvent\",  function(self, event, ...)\n            if event == \"PLAYER_ENTERING_WORLD\" then\n                WipeTables()\n            else\n                HandleEvent()\n            end\n    end)\n    \nend",
